// Localized names - accepts suit key (cups, pentacles, etc.)
export function getSuitName(suit: string, locale: string): string {
  if (locale === "en") return SUIT_KEY_EN[suit] || SUIT_NAMES_EN[suit] || suit;
  return SUIT_NAMES[suit as Suit] || suit;
}

// Localized names - accepts arcana key (major, minor)
export function getArcanaName(arcana: string, locale: string): string {
  if (locale === "en") return ARCANA_KEY_EN[arcana] || ARCANA_NAMES_EN[arcana] || arcana;
  return ARCANA_NAMES[arcana as Arcana] || arcana;
}

