export function getLocalizedCard(card: TarotCard, locale: string): TarotCard {
  if (locale === "pl") return card;
  
  const translation = CARD_TRANSLATIONS_EN[card.id];
  if (!translation) return { ...card, name: card.nameEn };
  
  return {
    ...card,
    name: card.nameEn,
    element: (ELEMENT_EN[card.element] || card.element) as any,
    zodiac: card.zodiac ? (ZODIAC_EN[card.zodiac] || card.zodiac) : undefined,
    planet: card.planet ? (PLANET_EN[card.planet] || card.planet) : undefined,
    uprightKeywords: translation.uprightKeywords,
    reversedKeywords: translation.reversedKeywords,
    uprightMeaning: translation.uprightMeaning,
    reversedMeaning: translation.reversedMeaning,
    imageDescription: translation.imageDescription,
    loveMeaning: translation.loveMeaning,
    healthMeaning: translation.healthMeaning,
    workMeaning: translation.workMeaning,
    advice: translation.advice,
  };
}
